import React from 'react';
import './Card.css';
import userimg from '../../assets/user.avif';
import { MoreHorizontal } from 'react-feather';

const Card = (props) => {
  const { ticket, groupBy } = props;
  const isUserGrouping = groupBy === 'user';

  const statusIcons = {
    Backlog: 'https://cdn-icons-png.flaticon.com/128/10905/10905332.png',
    Todo: 'https://cdn-icons-png.flaticon.com/128/446/446163.png',
    'In progress': 'https://cdn-icons-png.flaticon.com/128/5460/5460873.png',
    Done: 'https://cdn-icons-png.flaticon.com/128/5461/5461184.png',
    Cancelled: 'https://cdn-icons-png.flaticon.com/128/1008/1008968.png',
  };

  return (
    <div className='card'>
      <div className='card-top'>
        <p className='card-title'>
          {props.ticket.id}
          {!isUserGrouping && (
            <img src='https://cdn-icons-png.flaticon.com/128/1144/1144709.png' className='profile-pic' alt='profile-pic' />
          )}
        </p>
      </div>
      <div className='card-middle'>
        <p>
          {statusIcons[props.ticket.status] && groupBy !== 'status' && (
            <img src={statusIcons[props.ticket.status]} className='logo-pic' alt={props.ticket.status} />
          )}
        </p>
        <p className='card-desc bold'>{ticket.title}</p>
      </div>
      <div className='card-bottom'>
        {groupBy !== 'priority' && (
          <div className='component-1 pointer'>
            <MoreHorizontal className='icon' />
          </div>
        )}
        <div className='component-2 pointer'>
          <img
            src='https://cdn-icons-png.flaticon.com/128/5720/5720434.png'
            className='icon'
            alt='logo'
          />
          <p>{ticket.tag[0]}</p>
        </div>
      </div>
    </div>
  );
};

export default Card;
