import React from 'react';
import './Card.css';

import { Circle, MoreHorizontal } from 'react-feather';

const Card = (props) => {
  const { ticket, groupBy } = props;
  const isUserGrouping = groupBy === 'user';

  return (
    <div className='card'>
      <div className='card-top'>
        {isUserGrouping && (
          <>
            <img
              src="https://img.freepik.com/free-vector/illustration-user-avatar-icon_53876-5907.jpg?w=740&t=st=1694073291~exp=1694073891~hmac=75453a297eb80aadfa68f238f70825147d88f09eaceacec2a6e2bcc90445705e"
              className='profile-pic'
              alt='profile-pic'
            />
            <p className='user-name'>{ticket.userName}</p>
          </>
        )}
        <p className='card-title'>{ticket.id}</p>
      </div>
      <div className='card-middle'>
        <p className='card-desc bold'>{ticket.title}</p>
      </div>
      <div className='card-bottom'>
        <div className='component-1 pointer'>
          <MoreHorizontal className='icon' />
        </div>
        <div className='component-2 pointer'>
          <Circle className='icon' />
          <p>{ticket.tag[0]}</p>
        </div>
      </div>
    </div>
  );
};

export default Card;
