import React from 'react';
import './Card.css';
import userimg from "../../assets/user.avif"
import { Circle, MoreHorizontal } from 'react-feather';

const Card = (props) => {
  const { ticket, groupBy } = props;

  const isUserGrouping = groupBy === 'user';
  const logoReference = {
    "Backlog": <img src="https://cdn-icons-png.flaticon.com/128/3839/3839944.png" className='logo-pic' alt="Backlog" />,
    "Todo": <img src="https://cdn-icons-png.flaticon.com/128/3515/3515278.png" className='logo-pic' alt="Todo" />,
    "In progress": <img src="https://cdn-icons-png.flaticon.com/128/11478/11478715.png" className='logo-pic' alt="In progress" />,
    "Done": <img src="https://cdn-icons-png.flaticon.com/128/1828/1828640.png" className='logo-pic' alt="Done" />,
    "Cancelled": <img src="https://cdn-icons-png.flaticon.com/128/660/660252.png" className='logo-pic' alt="Cancelled" />
  };
  return (
    <div className='card'>
      <div className='card-top'>
      {props.groupingOption === "status" && logoReference[props.title]}
        <p className='card-title'>{ticket.id}</p>

        {!isUserGrouping && (
          <img src={userimg} className='profile-pic' alt='profile-pic' />
        )}
      </div>
      <div className='card-middle'>
        <p className='card-desc bold'>{ticket.title}</p>
      </div>
      <div className='card-bottom'>
        <div className='component-1 pointer'>
          <MoreHorizontal className='icon' />
        </div>
        <div className='component-2 pointer'>
          <Circle className='icon' />
          <p>{ticket.tag[0]}</p>
        </div>
      </div>
    </div>
  );
};

export default Card;
