import React, { useEffect, useState } from "react";
import './App.css';
import mid from "./assets/middle.jpeg";
import low from "./assets/low.jpeg";
import high from "./assets/high.jpeg";
import axios from "axios";
import Navbar from "./Components/Navbar/Navbar";
import Board from "./Components/Board/Board";

function App() {
  const [tickets, setTickets] = useState([]);
  const [users, setUsers] = useState([]);
  const [groupingOption, setGroupingOption] = useState(localStorage.getItem('groupBy') || "status");
  const [sortingOption, setSortingOption] = useState(localStorage.getItem('sortBy') || "priority");

  const priorityIcons = {
    0: "https://cdn-icons-png.flaticon.com/128/9974/9974562.png",
    1: low,
    2: mid,
    3: high,
    4: "https://cdn-icons-png.flaticon.com/128/9625/9625452.png"
  };

  const getUserLabel = (user) => (
    <div className="user-label">
      <img
        src="https://img.freepik.com/free-vector/illustration-user-avatar-icon_53876-5907.jpg?w=740&t=st=1694073291~exp=1694073891~hmac=75453a297eb80aadfa68f238f70825147d88f09eaceacec2a6e2bcc90445705e"
        className="user-pic"
        alt='profile-pic'
      />
      {user.name}
    </div>
  );

  useEffect(() => {
    getDetails();
  }, []);

  useEffect(() => {
    localStorage.setItem('groupBy', groupingOption);
    localStorage.setItem('sortBy', sortingOption);
  }, [groupingOption, sortingOption]);

  async function getDetails() {
    try {
      const { data } = await axios.get("https://api.quicksell.co/v1/internal/frontend-assignment");
      setTickets(data.tickets);
      setUsers(data.users);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  }

  const organizeTickets = () => {
    const organizedData = {};

    if (groupingOption === 'status' || groupingOption === 'priority' || groupingOption === 'user') {
      tickets.forEach(ticket => {
        if (!organizedData[ticket[groupingOption]]) {
          organizedData[ticket[groupingOption]] = [];
        }
        organizedData[ticket[groupingOption]].push(ticket);
      });
    }

    return organizedData;
  };

  const sortByPriority = (tickets) => [...tickets].sort((a, b) => b.priority - a.priority);
  const sortByTitle = (tickets) => [...tickets].sort((a, b) => a.title.localeCompare(b.title));

  const handleSortingChange = (event) => {
    setSortingOption(event.target.value);
  };

  const handleGroupingChange = (event) => {
    setGroupingOption(event.target.value);
  };

  const sortedTickets = (tickets) => {
    if (sortingOption === 'priority') {
      return sortByPriority(tickets);
    } else if (sortingOption === 'title') {
      return sortByTitle(tickets);
    }
    return tickets;
  };

  const boards = organizeTickets();

  return (
    <div className="app-container">
      <div className="app-navbar">
        <nav>
          <Navbar
            sortingOption={sortingOption}
            onSortingChange={handleSortingChange}
            groupingOption={groupingOption}
            onGroupingChange={handleGroupingChange}
          />
        </nav>
      </div>
      <div className="app-outer-container">
        <div className="app-boards">
          {Object.keys(boards).map(boardKey => (
            <Board
              key={boardKey}
              title={groupingOption === 'priority' ? (
                <>
                  <img src={priorityIcons[boardKey]} className="user-pic" alt="Priority" />
                  {' '}
                  {boardKey === "0" ? "No Priority" : `Priority ${boardKey}`}
                </>
              ) : groupingOption === "user" ? (
                getUserLabel(users.find(user => user.id === boardKey) || {})
              ) : (
                boardKey
              )}
              count={boards[boardKey].length}
              tickets={sortedTickets(boards[boardKey])}
              sortingOption={sortingOption}
              groupingOption={groupingOption}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
