import React from 'react';
import './Card.css';
import userimg from "../../assets/user.avif"
import { MoreHorizontal } from 'react-feather';
import circle from "../../assets/circle.png";
const Card = (props) => {
  const { ticket, groupBy } = props;

  const isUserGrouping = groupBy === 'user';
  const isStatusGrouping = groupBy === 'status';
  const isPriorityGrouping = groupBy === 'priority';
  const logoReference = {
    "Backlog": 'https://cdn-icons-png.flaticon.com/128/3839/3839944.png',
    "Todo": 'https://cdn-icons-png.flaticon.com/128/3515/3515278.png',
    "In progress": 'https://cdn-icons-png.flaticon.com/128/11478/11478715.png',
    "Done": 'https://cdn-icons-png.flaticon.com/128/1828/1828640.png',
    "Cancelled": 'https://cdn-icons-png.flaticon.com/128/660/660252.png'
  };
  const getFirstLetter = (name) => {
    return name.charAt(0).toUpperCase();
  };

  return (
    <div className='card'>
    <div className='card-top'>
      <p className='card-title'>{ticket.id}</p>
      
      {/* Display the first letter of the user's name */}
      {!isUserGrouping && (
        <div className='user-symbol'>
          {getFirstLetter(ticket.userName)}
        </div>
      )}
    </div>
    <div className='card-middle'>
      {!isStatusGrouping && (
        <img src={logoReference[ticket.status]} className='logo-pic' alt={ticket.status} />
      )}
      <p className='card-desc bold'>{ticket.title}</p>
    </div>

    <div className='card-bottom'>
      {!isPriorityGrouping && (
        <div className='component-1 pointer'>
          <MoreHorizontal className='icon' />
        </div>)}

      <div className='component-2 pointer'>
        <img src={circle} alt="" className='icon' />
        <p>{ticket.tag[0]}</p>
      </div>
    </div>
  </div>
);
};

export default Card;
