import React from 'react';
import './Board.css';
import CircleIcon from "./CircleIcon";
import { MoreHorizontal, Plus} from 'react-feather';

import Card from '../Card/Card';

const Board = (props) => {
  const logoReference = {
    "Backlog": <img src="https://cdn-icons-png.flaticon.com/128/3839/3839944.png" className='profile-pic' alt="Backlog" />,
    "Todo": <CircleIcon size={50} color="blue" />,
    "In progress": <img src="https://cdn-icons-png.flaticon.com/128/3839/3839944.png" className='profile-pic' alt="Backlog" />,
    "Done": <img src="https://cdn-icons-png.flaticon.com/128/3839/3839944.png" className='profile-pic' alt="Backlog" />,
    "Cancelled": <img src="https://cdn-icons-png.flaticon.com/128/3839/3839944.png" className='profile-pic' alt="Backlog" />
  };

  return (
    <div className='board'>
      <div className='board-top'>
        <div className='board-top-left'>
          {props.groupingOption === "status" && logoReference[props.title]}
          <p className='board-title bold'>{props.title}</p>
          <span className='board-count'>{props.count}</span>
        </div>
        <div className='board-top-right'>
          <MoreHorizontal className='icon' />
          <Plus className='icon' />
        </div>
      </div>
      <div className='card-container'>
        {props.tickets.map((ticket) => (
          <Card key={ticket.id} ticket={ticket} groupBy={props.groupingOption} />
        ))}
      </div>
    </div>
  );
};

export default Board;
