import React from 'react';
import './Card.css';
import { MoreHorizontal } from 'react-feather';
import circle from "../../assets/circle.png";
import defaultUserImage from "../../assets/user.avif"; // Import a default user image for cases where the user's image is not found
import userProfiles from '../../user-profiles'; // Import the folder containing user profile images

const Card = (props) => {
  const { ticket, groupBy } = props;

  const isUserGrouping = groupBy === 'user';
  const isStatusGrouping = groupBy === 'status';
  const isPriorityGrouping = groupBy === 'priority';
  const logoReference = {
    "Backlog": 'https://cdn-icons-png.flaticon.com/128/3839/3839944.png',
    "Todo": 'https://cdn-icons-png.flaticon.com/128/3515/3515278.png',
    "In progress": 'https://cdn-icons-png.flaticon.com/128/11478/11478715.png',
    "Done": 'https://cdn-icons-png.flaticon.com/128/1828/1828640.png',
    "Cancelled": 'https://cdn-icons-png.flaticon.com/128/660/660252.png'
  };

  // Get the user's profile image based on their first name
  const userProfileImage = userProfiles[ticket.userFirstName]
    ? userProfiles[ticket.userFirstName]
    : defaultUserImage;

  return (
    <div className='card'>
      <div className='card-top'>
        <p className='card-title'>{ticket.id}</p>
        {!isUserGrouping && (
          <img src={userProfileImage} className='profile-pic' alt='profile-pic' />
        )}
      </div>
      {/* ... rest of the code */}
    </div>
  );
};

export default Card;
